mapper.select:

        SELECT
	    coupon_id,
	 	code,
   		 name,
   		 img_address,
   		 discount,
   		 content,
   		 create_date,
   		 update_date,
   		 start_date,
   		 end_date,
   		 status,
   		 mer_cate_id,
   		 account_id,
   		 coupon_term_id
	FROM coupon_tb


		AND  code=#{code}


		AND  mer_cate_id=#{merCateId}


		AND  coupon_term_id=#{couponTermId}


		AND  account_id=#{accountId}


		AND  DATE(start_date)=DATE(#{startDate})


		AND  DATE(end_date)=DATE(#{endDate})


		AND  DATE(create_date)=DATE(#{createDate})


		AND  DATE(update_date)=DATE(#{updateDate})


		AND  status=#{status}

	AND ${orderName} 

	>= 


	  <= 

	(SELECT ${orderName} FROM coupon_tb 


		AND  code=#{code}


		AND  mer_cate_id=#{merCateId}


		AND  coupon_term_id=#{couponTermId}


		AND  account_id=#{accountId}


		AND  DATE(start_date)=DATE(#{startDate})


		AND  DATE(end_date)=DATE(#{endDate})


		AND  DATE(create_date)=DATE(#{createDate})


		AND  DATE(update_date)=DATE(#{updateDate})


		AND  status=#{status}


		ORDER BY 
			${orderName}  ${orderWay}
		LIMIT #{pageNum}, 1
		) 

	ORDER BY
		${orderName}  ${orderWay}
	LIMIT
		#{pageSize}


SELECT
 	count(coupon_id)
FROM coupon_tb


	AND  code=#{code}


	AND  mer_cate_id=#{merCateId}


	AND  coupon_term_id=#{couponTermId}


	AND  account_id=#{accountId}


	AND  DATE(start_date)=DATE(#{startDate})


	AND  DATE(end_date)=DATE(#{endDate})


	AND  DATE(create_date)=DATE(#{createDate})


	AND  DATE(update_date)=DATE(#{updateDate})


	AND  status=#{status}
