mapper.select:

        SELECT
	     mer_card_cipher_id,
	 	 code,
   		 img_address,
   		 create_date,
   		 mer_id
	FROM mer_card_cipher_tb


		AND  mer_id=#{merId}

	AND ${orderName} 

	>= 


	  <= 

	(SELECT ${orderName} FROM mer_card_cipher_tb 


		AND  mer_id=#{merId}


		ORDER BY 
			${orderName}  ${orderWay}
		LIMIT #{pageNum}, 1
		) 

	ORDER BY
		${orderName}  ${orderWay}
	LIMIT
		#{pageSize}
