mapper.select:
SELECT order_problem_answer_id, content, create_date, order_problem_id, account_id FROM order_problem_answer_tb
AND ${orderName}
>=
<=
(SELECT ${orderName} FROM order_problem_answer_tb ORDER BY ${orderName} ${orderWay} LIMIT #{pageNum}, 1 )
ORDER BY ${orderName} ${orderWay} LIMIT #{pageSize}
